<template>
  <div class="aboutus">
    <div class="aboutus-top">
      <div class="aboutus-top-img">
        <img
          src="data:image/png;base64,"
          alt
        />
      </div>
      <div class="aboutus-top-edition">2.3版本</div>
    </div>
    <div class="aboutus-intro">
      <div class="aboutus-intro-text">【问易台介绍】</div>
      <div class="aboutus-introtext" v-html="introduce"></div>
    </div>
    <div class="aboutus-contact">
      <div class="aboutus-contact-text">【联系方式】</div>
      <div class="aboutus-contactext" v-html="contact"></div>
    </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      contact: "",
      introduce: ""
    };
  },
  methods: {},
  created() {
    this.$http.post("about_us").then(res => {
      this.contact = res.data.data.contact["0"].content;
      this.introduce = res.data.data.introduce["0"].content;
    });
  }
};
</script>