<template>
  <div class="topicdetail">
    <div class="topicdetail-banner">
      <div class="reHome" @click="reHome">
        <img
          src="data:image/png;base64,"
          alt
        />
        <span>首页</span>
      </div>
      <img alt src="http://kccdn.ywhwl.com/img/VaOmk9Jr2zkapM0En3wQUyjQEzJRXkCxvmHibULd.png" />
      <div class="topicdetail-banner-title">{{topicDetail.title}}</div>
    </div>
    <div class="topicdetail-test">
      <span>热</span>
      <span>今日{{topicDetail.virtual_people}}人测算</span>
    </div>
    <div class="topicdetail-introduction">
      <div class="topicdetail-introduction-img">
        <img
          src="data:image/png;base64,"
          alt
        />
      </div>
      <div class="topicdetail-introduction-text">{{topicDetail.content}}</div>
    </div>
    <div class="topicdetail-explain">
      <div class="topicdetail-explain-img">
        <img
          src="data:image/png;base64,"
          alt
        />
      </div>
      <div class="topicdetail-explain-text">{{topicDetail.introduce}}</div>
      <div class="topicdetail-explain-tip">
        <div class="topicdetail-explain-tip-img">
          <img
            src="data:image/png;base64,"
            alt
          />
        </div>
        <div class="topicdetail-explain-tip-leftt">
          <img
            src="data:image/png;base64,"
            alt
          />
        </div>
        <div class="topicdetail-explain-tip-leftb">
          <img
            src="data:image/png;base64,"
            alt
          />
        </div>
        <div class="topicdetail-explain-tip-rightt">
          <img
            src="data:image/png;base64,"
            alt
          />
        </div>
        <div class="topicdetail-explain-tip-rightb">
          <img
            src="data:image/png;base64,"
            alt
          />
        </div>
        <div class="topicdetail-explain-tip-title">温馨提示:</div>
        <div class="topicdetail-explain-tip-text">
          <span class="topicdetail-explain-tip-text-one">请各位添加</span>
          <span class="topicdetail-explain-tip-text-two">问易君微信号:</span>
          <input value="wenyijunwx" id="weixin" class="topicdetail-explain-tip-text-three" />
          <button
            data-clipboard-text="wenyijunwx"
            data-clipboard-demo
            data-clipboard-action="copy"
            class="topicdetail-explain-tip-text-copy"
          >一键复制</button>
        </div>
        <div
          class="topicdetail-explain-tip-int"
        >申请进群参加不定期群福利,抢红包,抢免单及优惠券,更有专业问易老师不定期免费案例分享以及解惑咨询,欢迎下单的您加入问易用户交流</div>
      </div>
    </div>
    <!---->
    <div class="masterdetail-relevant">
      <div class="masterdetail-relevant-title">相关话题：</div>
      <div class="masterdetail-relevant-lists">
        <div
          class="masterdetail-relevant-list"
          v-for="(item,index) in topicRelated"
          :key="index"
          @click="toggle(item.id)"
        >
          <div class="masterdetail-relevant-list-img">
            <img :src="item.thumb" alt />
          </div>
          <div class="masterdetail-relevant-list-text">
            <div class="masterdetail-relevant-list-text-title">
              {{item.content}}
              <span class="hot" v-if="item.is_rem===1">热</span>
            </div>
            <div class="masterdetail-relevant-list-text-num">今日{{item.virtual_people}}人已测</div>
          </div>
          <div class="masterdetail-relevant-list-price">{{item.price}}元</div>
        </div>
      </div>
    </div>
    <div class="masterdetail-pay">
      <div class="masterdetail-pay-money">
        <div>
          原价：
          <span>¥{{topicDetail.old_price}}</span>
        </div>
        <div>
          优惠价：
          <span>¥{{topicDetail.price}}</span>
        </div>
      </div>
      <div class="masterdetail-pay-btn">
        <div @click="reSureOrder">立即下单</div>
      </div>
    </div>
  </div>
</template>


<script>
import Clipboard from "clipboard";
import { Toast } from "mint-ui";
export default {
  components: {
    Toast: Toast
  },
  data() {
    return {
      topicId: this.$route.query.topicid,
      topicDetail: {
        title: "",
        content: "",
        virtual_people: "",
        introduce: "",
        old_price: 0,
        price: 0
      },
      topicRelated: [],
      PageId: 0,
      commentCount: 0,
      commentList: []
    };
  },
  methods: {
    reHome() {
      this.$router.push({
        name: "home"
      });
    },
    copyText() {
      var that = this;
      if (Clipboard.isSupported()) {
        var clipboardDemos = new Clipboard(
          "[data-clipboard-demo]"
        ); /*创建方法*/
        /*复制成功回调*/
        clipboardDemos.on("success", function(e) {
          e.clearSelection();
          Toast({
            message: "复制成功",
            position: "bottom",
            duration: 2000
          });

          clipboardDemos.destroy();
        });
        /*复制失败回调*/
        clipboardDemos.on("error", function(err) {
          clipboardDemos.destroy();
        });
      } else {
        /*浏览器不支持时，进入手动复制方法*/

        Toast({
          message: "您的浏览器暂不支持此功能！请手动复制",
          position: "bottom",
          duration: 2000
        });
      }
    },
    // 获取话题详情
    getTopicDetail(top_id) {
      this.$http.post("topic_detail", { top_id: top_id }).then(res => {
        if (res.data.code == 200) {
          this.topicDetail = res.data.data.top_info;
          this.topicRelated = res.data.data.top_related;
        }
      });
    },
    // 获取评论
    getComment(top_id, PageId) {
      this.$http
        .post("topic_detail_discuss", { top_id: top_id, PageId: PageId })
        .then(res => {
          if (res.data.data !== []) {
            this.commentCount = res.data.data.num_all;
            var arr = res.data.data.comment;
            if (arr.length !== 0) {
              arr.forEach(element => {
                this.commentList.push(element);
              });
            }
          }
        });
    },
    //  获取更多评论
    getMoreComment() {
      this.PageId++;
      this.getComment(this.topicId, this.PageId);
    },
    // 下单
    reSureOrder() {
      var topicOrder = {
        topicId: this.topicId,
        ...this.topicDetail,
        type: "话题快测"
      };
      localStorage.setItem("orderMessage", JSON.stringify(topicOrder));
      this.$router.push({ name: "sureOrder" });
    },

    // 切换服务
    toggle(id) {
      this.getTopicDetail(id);
      window.scroll(0, 0);
    }
  },
  created() {
    // 获取话题详情
    this.getTopicDetail(this.topicId);
    // 获取评论
    this.getComment(this.topicId, this.PageId);
  },
  mounted() {
    this.copyText();
  }
};
</script>